/** Global definitions for developement **/

// for style loader
declare module '*.jpg' {
  const content: string;
  export default content;
}
declare module '*.png' {
  const content: string;
  export default content;
}
declare module '*.svg' {
  const content: string;
  export default content;
}

// for .sbc that are XMLs
declare module '*.sbc' {
  const content: string;
  export default content;
}
declare module '*.data' {
  const content: string;
  export default content;
}
